svg.script:
                    const svgAnim = document.querySelectorAll('.svg-animation');
                    const svgObserver = new IntersectionObserver(
                        entries => {
                            entries.forEach(entry => {
                                entry.target.classList.toggle('showAnim', entry.isIntersecting)
                                if (entry.isIntersecting) svgObserver.unobserve(entry.target);
                            })
                        },
                        {
                            threshold:1,
                        }
                    )
                    
                    svgAnim.forEach(inv => {
                    svgObserver.observe(inv);
               });
